import React, { useState, useEffect } from "react";
import { Form, Alert, InputGroup, Button, ButtonGroup } from "react-bootstrap";
import NewsDataService from "./services/news.services";

const AddNews = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [link, setLink] = useState("");
  const [message, setMessage] = useState({ error: false, msg: "" });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    if (title === "" || content === "" || link === "") {
      setMessage({ error: true, msg: "All fields are mandatory" });
    }
    const newNews = {
      title,
      content,
      link,
    };
    console.log(newNews);

    try {
        if (id !== undefined && id !== "") {
            await NewsDataService.updateNews(id, newNews);
            setNewsId("");
            setMessage({ error: false, msg: "Updated successfully!" });
          } else {
            await NewsDataService.addNews(newNews);
            setMessage({ error: false, msg: "New Book added successfully!" });
          }
    } catch (err) {
      setMessage({ error: true, msg: err.message });
    }

    setTitle("");
    setContent("");
    setLink("");
  };

  return (
    <>
      <div className="p-4 box">
      {message?.mg && (
        <Alert
          variant={message?.error ? "danger" : "success"}
          dismissible
          onClose={() => setMessage("")}
        >
          {""}
          {message?.msg} 
        </Alert>
      )}
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3" controlId="formNewsTitle">
            <InputGroup>
              <InputGroup.Text id="formNewsTitle">B</InputGroup.Text>
              <Form.Control
                type="text"
                placeholder="News Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </InputGroup>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formNewsContent">
            <InputGroup>
              <InputGroup.Text id="formNewsContent">A</InputGroup.Text>
              <Form.Control
                type="text"
                placeholder="News Content"
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
            </InputGroup>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formNewsLink">
            <InputGroup>
              <InputGroup.Text id="formNewsLink">A</InputGroup.Text>
              <Form.Control
                type="text"
                placeholder="News Link"
                value={link}
                onChange={(e) => setLink(e.target.value)}
              />
            </InputGroup>
          </Form.Group>
          <div className="d-grid gap-2">
            <Button variant="primary" type="Submit">
              Add/ Update
            </Button>
          </div>
        </Form>
      </div>
    </>
  );
};

export default AddNews;
