import React, { useState } from "react";
import app from './firebase'
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import Dashboard from "./Dashboard";

function Login({ signed }) {
    const auth = getAuth(app);

    let navigate = useNavigate();

    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    // const signUp = () => {
    //     createUserWithEmailAndPassword(auth, email, password)
    //     .then((userCredential) => {
    //         // Signed in 
    //         const user = userCredential.user;
    //         console.log(user);
    //         alert("Success")
    //         // ...
    //     })
    //     .catch((error) => {
    //         const errorCode = error.code;
    //         // const errorMessage = error.message;
    //         alert(errorCode)
    //         // ..
    //     })
    // }
    const signIn = () => {
    signInWithEmailAndPassword(auth, email, password)
        .then((userCredential) => {
            // Signed in 
             let signed = false;            const user = userCredential.user;
            console.log(user);
            alert("Signed In");
            // ...
        })
        .catch((error) => {
             let signed = false;
            const errorCode = error.code;
            alert(errorCode, signed)
        });

    }

  return (
    <section class="h-100">
      <div class="container h-100">
        <div class="row justify-content-sm-center h-100">
          <div class="col-xxl-4 col-xl-5 col-lg-5 col-md-7 col-sm-9">
            <div class="text-center my-5 logoh1">
              <a href="/">
                <h1>Heron Bend</h1>
                <h4>Login Portal</h4>
              </a>
            </div>
            <div class="card shadow-lg">
              <div class="card-body p-5">
                <h1 class="fs-4 card-title fw-bold mb-4">Login</h1>
                <div
                  method="POST"
                  class="needs-validation"
                >
                  <div class="mb-3">
                    <label class="mb-2 text-muted" for="email">
                      E-Mail Address
                    </label>
                    <input
                      onChange={(e) => setEmail(e.target.value)}
                      id="email"
                      type="email"
                      class="form-control"
                      name="email"
                      required
                      autofocus
                    />
                    <div class="invalid-feedback">Email is invalid</div>
                  </div>

                  <div class="mb-3">
                    <div class="mb-2 w-100">
                      <label class="text-muted" for="password">
                        Password
                      </label>
                    </div>
                    <input
                      onChange={(e) => setPassword(e.target.value)}
                      id="password"
                      type="password"
                      class="form-control"
                      name="password"
                      required
                    />
                    <div class="invalid-feedback">Password is required</div>
                  </div>

                  <div class="d-flex align-items-center">
                    <button class="btn btn-primary ms-auto" onClick={ () => {
                            signIn();
                            <Dashboard authorized={signed} />
                            navigate("/dashboard");
                        } } type="submit">
                      Login
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Login;
