   render() {

	return (

		<Calendar

			events={list}

			startAccessor="start"

			endAccessor="end"

			defaultDate={moment().toDate()}

			eventPropGetter={event => {

				const eventData = list.find(ot => ot.id === event.id);

				const backgroundColor = eventData && eventData.color;

				return { style: { backgroundColor } };

			}}

		/>

	)

   }
}
export default Calender;
